#include "Objects/Core.hh"

using namespace std;

namespace baboon {


	Core::Core()
	: HitCompositeObject(),
	  TypedObject("Core"),
	  buildConcentration(0) {

	}

	Core::~Core() {

	}

	Return Core::SetBuildConcentration( const double c ) {

		if( c <= 0 )
			return BABOON_INVALID_PARAMETER("Concentration can't be < 0!");

		buildConcentration = c;

		return BABOON_SUCCESS();
	}

}  // namespace 

