#include "Objects/Hit.hh"
#include "Objects/Cluster.hh"

using namespace std ;

namespace baboon {

	Hit::Hit( const HitParameters &params ) {

		position = params.position;
		fThreshold = params.threshold;
		ijk = params.ijk;
		type = params.type;
		time = params.time;
		hitTag = fUndefined;
	}


	Hit::~Hit() {}

	Return Hit::SetPosition( const ThreeVector& pos ) {

		position = pos;
		return BABOON_SUCCESS();
	}


	Return Hit::SetThreshold( const HitThreshold &fThr ) {

		fThreshold = fThr;
		return BABOON_SUCCESS();
	}


	Return Hit::SetHitTag( const Tag &fTag ) {

		hitTag = fTag;
		return BABOON_SUCCESS();
	}


	Return Hit::SetWeight( const int &w ) {

		weight = w;
		return BABOON_SUCCESS();
	}


	Return Hit::SetIJK( int I , int J , int K ) {

		ijk.clear();
		ijk.push_back(I);
		ijk.push_back(J);
		ijk.push_back(K);
		return BABOON_SUCCESS();
	}


	Return Hit::SetIJK( const IntVector &vec ) {

		ijk = vec;
		return BABOON_SUCCESS();
	}

	Return Hit::SetType( int t ) {

		type = t;
		return BABOON_SUCCESS();
	}

	Return Hit::SetTime( double t ) {

		time = t;
		return BABOON_SUCCESS();
	}



}

