#include "Objects/TrackInfo.hh"

#include "EVENT/LCGenericObject.h"
#include "IMPL/LCGenericObjectImpl.h"

namespace baboon {


	TrackInfo::TrackInfo()
	{
		charge = 0;
	}

	TrackInfo::~TrackInfo()
	{

	}

	EVENT::LCGenericObject *TrackInfo::CreateGenericObject() const
	{

		IMPL::LCGenericObjectImpl *genericObj = new IMPL::LCGenericObjectImpl( 1 , 6 , 0 );

		genericObj->setIntVal( 0 , charge );

		genericObj->setFloatVal( 0 , enteringPoint.x() );
		genericObj->setFloatVal( 1 , enteringPoint.y() );
		genericObj->setFloatVal( 2 , enteringPoint.z() );

		genericObj->setFloatVal( 3 , momentum.x() );
		genericObj->setFloatVal( 4 , momentum.y() );
		genericObj->setFloatVal( 5 , momentum.z() );

		return genericObj;

	}


	void TrackInfo::TakeInfoFrom( EVENT::LCGenericObject *obj )
	{

		if( ! obj )
			return;

		charge = obj->getIntVal( 0 );

		enteringPoint.setX( obj->getFloatVal( 0 ) );
		enteringPoint.setY( obj->getFloatVal( 1 ) );
		enteringPoint.setZ( obj->getFloatVal( 2 ) );

		momentum.setX( obj->getFloatVal( 3 ) );
		momentum.setY( obj->getFloatVal( 4 ) );
		momentum.setZ( obj->getFloatVal( 5 ) );

	}


}  // namespace 

